insert into aplicacao.seriado (id, sinopse, classificacao_etaria, nome_diretor, ano, titulo, desenho, imagem_path) values(-1,'Matthew Michael Murdock (Charlie Cox) é um jovem atleta e excelente aluno. Ainda na adolescência, um acidente envolvendo um caminhão que carregava lixos tóxicos o deixou cego e fez com que ele desenvolvesse vários sentidos. Quando Matt decide vestir o uniforme e adotar o nome Demolidor (Daredevil), leva uma vida dupla: é advogado durante o dia, e, à noite, protege as ruas de Hells Kitchen, seu bairro em Nova York.',18,'Phil Abraham',2015,'Demolidor',false,'/resources/layout/imagens/folder/demolidor.jpg');
insert into aplicacao.seriado (id, sinopse, classificacao_etaria, nome_diretor, ano, titulo, desenho, imagem_path) values(-2,'Há muito tempo, em um tempo esquecido, uma força destruiu o equilíbrio das estações. Em uma terra onde os verões podem durar vários anos e o inverno toda uma vida, as reivindicações e as forças sobrenaturais correm as portas do Reino dos Sete Reinos. A irmandade da Patrulha da Noite busca proteger o reino de cada criatura que pode vir de lá da Muralha, mas já não tem os recursos necessários para garantir a segurança de todos. Depois de um verão de dez anos, um inverno rigoroso promete chegar com um futuro mais sombrio. Enquanto isso, conspirações e rivalidades correm no jogo político pela disputa do Trono de Ferro, o símbolo do poder absoluto.',18,'Tim Van Patten',2011,'Game of Trhones',false,'/resources/layout/imagens/folder/got.jpg');
insert into aplicacao.seriado (id, sinopse, classificacao_etaria, nome_diretor, ano, titulo, desenho, imagem_path) values(-3,'Spartacus: Blood and Sand, conta a história de Spartacus (Andy Whitfield), o escravo de guerra romano que se tornou um gladiador e liderou a mais famosa revolução da Roma Antiga. Quando é separado de sua esposa e os dois são colocados a venda, Spartacus é jogado em uma Arena para a diversão da plebe, mas surpreende a todos quando vence as quatro batalhas. E ele não irá parar enquanto não reencontrar sua esposa.',18,'Sam Raimi',2010,'Spartacus',false,'/resources/layout/imagens/folder/spartacus.jpg');
insert into aplicacao.seriado (id, sinopse, classificacao_etaria, nome_diretor, ano, titulo, desenho, imagem_path) values(-4,'Depois de presenciar o suicídio de uma mulher (que é exatamente como ela) em uma estação de trem, Sarah Manning (Tatiana Maslany) faz o que qualquer um faria: assume a identidade da suicida para tentar resolver os próprios problemas financeiros. Mas logo ela descobre que está no centro de um mistério que vai mudar sua vida, quando se vê cara a cara com mais três mulheres idênticas a ela. Todas são clones, e precisam salvar as próprias peles enquanto tentam descobrir quem são os responsáveis pelos experimentos genéticos.',16,'John Fawcett',2013,'Orphan Black',false,'/resources/layout/imagens/folder/orphanblack.jpg');
insert into aplicacao.seriado (id, sinopse, classificacao_etaria, nome_diretor, ano, titulo, desenho, imagem_path) values(-5,'Grupos de pessoas ao redor do mundo que estão ligadas mentalmente, e precisam achar uma maneira de sobreviver sendo caçados por aqueles que os veem como uma ameaça para a ordem mundial.',16,'Joseph Mawle',2015,'Sense8',false,'/resources/layout/imagens/folder/sense8.png');
insert into aplicacao.seriado (id, sinopse, classificacao_etaria, nome_diretor, ano, titulo, desenho, imagem_path) values(-6,'Após a prisão de Lincoln Burrows (Dominic Purcell), condenado por um crime que não cometeu, o engenheiro Michael Scofield (Wentworth Miller) bola um plano para tirar o irmão da cadeia. Enviado para Fox River ao lado de Lincoln, Michael começa a executar a sua estratégia, mas logo percebe que está no meio de uma perigosa conspiração. Para garantir a liberdade da sua família, ele precisará enganar a Dra. Sara Tancredi (Sarah Wayne Callies) e se associar a criminosos condenados, como Fernando Sucre (Amaury Nolasco), Theodore (T-Bag) Bagwell (Robert Knepper) e John Abruzzi (Peter Stormare).',16,'Brett Ratner',2005,'Prison Break',false,'/resources/layout/imagens/folder/prisonbreak.jpg');
insert into aplicacao.seriado (id, sinopse, classificacao_etaria, nome_diretor, ano, titulo, desenho, imagem_path) values(-7,'Um grupo diverso de brilhantes e controlados recrutas chega à base do FBI em Quantico para o treinamento. Eles são considerados alguns dos melhores agentes, mas, paradoxalmente, um deles se torna suspeito de planejar o maior ataque em Nova York desde o dia 11 de setembro.',14,'Patrick R. Norris',2015,'Quantico',false,'/resources/layout/imagens/folder/quantico.jpg');
insert into aplicacao.seriado (id, sinopse, classificacao_etaria, nome_diretor, ano, titulo, desenho, imagem_path) values(-8,'Michaela, Wes, Laurel e Patrick são ambiciosos calouros de Direito da prestigiada academia East Coast Law School, onde apenas os melhores alunos podem participar de casos reais. Eles competem entre si para conseguir a atenção da carismática e sedutora Professora Annalise DeWitt (Viola Davis), na aula de Direito Criminal 1, também conhecida como Como Se Livrar de Um Assassinato',14,'Bill DElia',2014,'How to Get Away With Murder',false,'/resources/layout/imagens/folder/howtogetawaywithmurder.jpg');
insert into aplicacao.seriado (id, sinopse, classificacao_etaria, nome_diretor, ano, titulo, desenho, imagem_path) values(-9,'Na cidade fictícia de Storybrooke, no Maine, Regina (Lana Parrilla) é uma rainha má que rouba memórias graças à maldição obtida por meio de Rumplestiltskin (Robert Carlyle). Suas vítimas viveram, portanto, uma realidade imutável durante 28 anos, sem ter qualquer noção de sua idade. Todas as esperanças estão depositadas em Emma Swan (Jennifer Morrison), filha da Branca de Neve (Ginnifer Goddwin) e do Príncipe Encantado (Josh Dallas). Ela é a única pessoa com a capacidade de quebrar a maldição e recuperar as lembranças perdidas, pois foi transportada do mundo de conto de fadas antes de ser atingida pelo feitiço.',12,'Mark Mylod',2011,'Once Upon a Time',false,'/resources/layout/imagens/folder/onceuponatime.jpg');
insert into aplicacao.seriado (id, sinopse, classificacao_etaria, nome_diretor, ano, titulo, desenho, imagem_path) values(-10,'Seis jovens são unidos por laços familiares, românticos e, principalmente, de amizade, enquanto tentam vingar em Nova York. Rachel é a garota mimada que deixa o noivo no altar para viver com a amiga dos tempos de escola Monica, sistemática e apaixonada pela culinária. Monica é irmã de Ross, um paleontólogo que é abandonado pela esposa, que descobriu ser lésbica. Do outro lado do corredor do apartamento de Monica e Rachel, moram Joey, um ator frustrado, e Chandler, de profissão misteriosa. A turma é completa pela exótica Phoebe.',12,'James Burrows',1994,'Friends',false,'/resources/layout/imagens/folder/friends.jpg');
